SELECT OID, HDNUNUSUALTYPENAME, D1_DEPT, D1_NAME, D1_TITLE, D2_DEVICENAME, D2_LOTNO, D2_LOTQTY, 
                  D2_PACKAGETYPE, D2_QTY, D3_ACTION, D3_DEPT, D3_OWNER, D4_DESC, D4_ROOTCAUSE 
                  FROM 8DREPORT WHERE HDNUNUSUALTYPENAME LIKE '%破片%' OR D4_DESC LIKE '%破片%' OR D4_ROOTCAUSE LIKE '%破片%';
